import React from 'react'
import classNames from 'classnames'

export default class Icon extends React.Component {
    constructor(props) {
        super(props)
        this.state = {}
    }

    render() {
        const {type, className, ...others} = this.props
        const classes = classNames({
            [`tf-${type}`]: true,
            [className]: className
        })

        return (
            <i {...others} className={classes}></i>
        )
    }
}

Icon.defaultProps = {
    // @desc 类型,用来区分不同图标
    type: 'post',

    // @desc 样式
    style: {}
}